  }

  render() {
    const { editorState } = this.state;

    return (
      <div className="txtEditor">
        <Editor
          editorState={editorState}
          onEditorStateChange={this.onEditorStateChange}
        />

       <input type="text"/>
        <button onClick={()=>{this.setData()}}>Save</button>
      </div>
    );
  }
}
